import React, { Component } from "react";
import LaskuriItems from "./LaskuriItems";
import "./Laskuri.css";

class Laskuri extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: []
    };

    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  addItem(e) {
    if (this._inputElementa.value !== "") {
      var newItem = {
        text: this._inputElementa.value,
        key: Date.now()
      };

      this.setState((prevState) => {
        return { 
          items: prevState.items.concat(newItem) 
        };
      });

      this._inputElementa.value = "";
    }

    console.log(this.state.items);

    e.preventDefault();
  }

  addItem(e) {
    if (this._inputElementb.value !== "") {
      var newItem = {
        text: this._inputElementb.value,
        key: Date.now()
      };

      this.setState((prevState) => {
        return { 
          items: prevState.items.concat(newItem) 
        };
      });

      this._inputElementb.value = "";
    }

    console.log(this.state.items);

    e.preventDefault();
  }

  deleteItem(key) {
    var filteredItems = this.state.items.filter(function (item) {
      return (item.key !== key);
    });

    this.setState({
      items: filteredItems
    });
  }

  render() {
    return (
      <div className="LaskuriMain">
        <div className="header">
          <form onSubmit={this.addItem}>
            <input ref={(a) => this._inputElementa = a} placeholder="selite">
            </input>
            <input ref={(b) => this._inputElementb = b} placeholder="määrä">
            </input>
            <input ref={(c) => this._inputElementc = c} placeholder="yksikköhinta">
            </input>
            <input ref={(d) => this._inputElementd = d} placeholder="alennus">
            </input>
            <input ref={(e) => this._inputElemente = e} placeholder="summa">
            </input>
            <button type="submit">lisää</button>
          </form>
        </div>
        <LaskuriItems entries={this.state.items} delete={this.deleteItem}/>
      </div>
    );
  }
}

export default Laskuri;
