import React, { Component } from "react";
import LaskuriItems from "./LaskuriItems";
import "./Laskuri.css";

class Laskuri extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: []
    };

    this.addItem = this.addItem.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
  }

  addItem(e) {
    if (this._inputElement.value !== "") {
      var newItem = {
        text: this._inputElement.value,
        key: Date.now()
      };

      this.setState((prevState) => {
        return { 
          items: prevState.items.concat(newItem) 
        };
      });

      this._inputElement.value = "";
    }

    console.log(this.state.items);

    e.preventDefault();
  }

  deleteItem(key) {
    var filteredItems = this.state.items.filter(function (item) {
      return (item.key !== key);
    });

    this.setState({
      items: filteredItems
    });
  }

  render() {
    return (
      <div className="LaskuriMain">
        <div className="header">
          <form onSubmit={this.addItem}>
            <input ref={(a) => this._inputElement = a} placeholder="selite">
            </input>
            <input ref={(b) => this._inputElement = b} placeholder="määrä">
            </input>
            <input ref={(c) => this._inputElement = c} placeholder="yksikköhinta">
            </input>
            <input ref={(d) => this._inputElement = d} placeholder="alennus">
            </input>
            <input ref={(e) => this._inputElement = e} placeholder="summa">
            </input>
            <button type="submit">lisää</button>
          </form>
        </div>
        <LaskuriItems entries={this.state.items} delete={this.deleteItem}/>
      </div>
    );
  }
}

export default Laskuri;
